import { Model, DataTypes, Sequelize } from 'sequelize';

export async function PhotoFactory(sequelize: Sequelize, config: { SYNC_SEQUELIZE_MODELS: string }) {
    class Photo extends Model {
        public readonly id!: number; // Note that the `null assertion` `!` is required in strict mode.
        public readonly userId!: string;
        public readonly albumId!: string;

        public readonly name: string;

        // timestamps!
        public readonly createdAt!: Date;
        public readonly updatedAt!: Date;

        public readonly facebook: string;

        public readonly alt_text: string;
        public readonly link!: string;

        public readonly width!: number;
        public readonly height!: number;
    }

    Photo.init({
        id: {
            type: DataTypes.INTEGER.UNSIGNED,
            autoIncrement: true,
            primaryKey: true,
        },
        width: {
            type: DataTypes.INTEGER.UNSIGNED,
            allowNull: false,
        },
        height: {
            type: DataTypes.INTEGER.UNSIGNED,
            allowNull: false,
        },
        name: {
            type: new DataTypes.STRING(128),
            allowNull: true,
        },
        facebook: {
            type: new DataTypes.STRING(10000),
            allowNull: true,
        },
        alt_text: {
            type: new DataTypes.STRING(512),
            allowNull: true,
        },
        link: {
            type: new DataTypes.STRING(256),
            allowNull: false,
        },
        albumId: {
            type: DataTypes.INTEGER.UNSIGNED,
            allowNull: false,
        },
        userId: {
            type: DataTypes.INTEGER.UNSIGNED,
            allowNull: false,
        }
    }, {
            tableName: 'Photo',
            sequelize
        }
    );

    if (config.SYNC_SEQUELIZE_MODELS) {
        await Photo.sync({ force: true })
    }

    return Photo;
}

type Unpromise<T extends Promise<any>> = T extends Promise<infer U> ? U : never;
export type PhotoType = Unpromise<ReturnType<typeof PhotoFactory>>