import { Sequelize } from 'sequelize'

export function sequelizeFactory(config: PG_CONFIG) {
    return new Sequelize(
        config.PG_DB_NAME,
        config.PG_USER,
        config.PG_PASSWORD, {
            "dialect": "postgres",
            "host": config.PG_HOST,
            "port": parseInt(config.PG_PORT),
        }
    )
}

export interface PG_CONFIG {
    PG_DB_NAME: string,
    PG_USER: string,
    PG_PASSWORD: string,
    PG_HOST: string,
    PG_PORT: string,
}