import { Router } from 'express'
import { AlbumType } from 'database/Album';
import { PhotoType } from 'database/Photo';

export function photosRouterFactory(router: Router, Album: AlbumType, Photo: PhotoType) {
    router.get('/', function (req, res) {
        console.log(req.session)
        res.send('Hello World')
    })

    router.get('/albums', async function (req, res) {
        res.json(await Album.findAll())
    })

    router.get('/photos', async function (req, res) {
        res.json(await Photo.findAll())
    })

    return router;
}